}

// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )
//declare namespace
//holder.declareNamespace("gvp1","http://schemas.datacontract.org/2004/07/GVP.GAL.Model")

// get arguments
def argUniqueId = holder.getNodeValue( "//gvp:uniqueId" )
def argBlockType = holder.getNodeValue( "//gvp1:DataBlockType[1]" )

log.info "El valor es:" + argBlockType

if(argUniqueId == "user1"){
	return "OTT_retriever all OTT INFO"
}else if(argUniqueId == "playBack"){
	return "GetUserDetailedInfo - PlayBackTime"		
}else if(argUniqueId == "UserPPV"){
	return "GetUserDetailedInfo - PPV"		
}else if(argUniqueId == "playBack1"){
	return "GetUserDetailedInfo - PlayBackTime HistoryPurchase"		
}else if(argUniqueId == "user2"){
	return "IPTV_retriever all IPTV INFO"
}else if(argUniqueId == "user3"){
	return "OTT_retriever user attributtes"
}else if(argUniqueId == "user4"){
	return "IPTV_retriever user attributes"
}else if(argUniqueId == "user5"){
	return "OTT_retriever OTT ACTIVE PURCHASE"	
}else if(argUniqueId == "user6"){
	return "IPTV_retriever IPTV ACTIVE PURCHASE"
}else if(argUniqueId == "user7"){
	return "OTT_retriever OTT HISTORIC PURCHASE"	
}else if(argUniqueId == "user8"){
	return "IPTV_retriever IPTV HISTORIC PURCHASE"			

}else if(argUniqueId == "UserIsAlreadyDisabled"){
	return "IPTV - user disabled"		

}else if(argUniqueId == "UserTimeShift"){	
	return "IPTV Profile - retriever Time Shift Info"	

}else if(argUniqueId == "UserAttributes" && argBlockType=="UserAttributes"){	
	return "IPTV Attributes - retriever Info"	

}else if(argUniqueId == "UserAttributesTimeShift"){	
	return "IPTV Attributes - retriever Time Shift Info"	

}else if(argUniqueId == "UserProfile"){	
	return "IPTV Profile - retriever Info"

}else{

	return "IPTV - OTT user does not exist"
}

