con:mockOperation.con:dispatchPath:
// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )

// get arguments and sum
def argUniqueId = holder.getNodeValue( "//gvp1:UniqueId" )
def argEmail = holder.getNodeValue( "//gvp1:Email" )

//return "MultipleErrorsDetectedByUMG"

//
/*if (argEmail==null || argEmail ==""){
	return "MissingMandatoryEmail"
}*/

if(argUniqueId == "UserAlreadyExists"){	
	return "UserAlreadyExists"	
}
else if(argEmail == "mail@mail.mail"){
	return "InvalidEmail"	
}
else if(argUniqueId == "UserIsAlreadyEnabled"){
	return "UserIsAlreadyEnabled"	
}
else if(argUniqueId == "UserIsAlreadyDisabled"){
	return "UserIsAlreadyDisabled"
}
else if(argUniqueId == "CannotDeleteEnabledUser"){
	return "CannotDeleteEnabledUser"
}
else if(argUniqueId == "MultipleErrorsDetectedByUMG"){
	return "MultipleErrorsDetectedByUMG"
}
else if(argUniqueId == "AccountAlreadyExists"){
	return "AccountAlreadyExists"
}
else if(argUniqueId == "PrincipalIdDoesNotExist"){
	return "PrincipalIdDoesNotExist"
}
else if(argUniqueId == "InvalidPassword"){
	return "InvalidPassword"

}else{
	requestContext.UniqueId = argUniqueId	
	return "OK - UniqueId"
}

con:query.con:query:
declare namespace v1='http://www.telefonica.com/schemas/UNICA/SOAP/common/v1';
declare namespace v6='http://www.telefonica.com/schemas/UNICA/SOAP/OProv_Management/v6/';
declare namespace gvp='GVP.GAL';
declare namespace soapenv='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace gvp1='http://schemas.datacontract.org/2004/07/GVP.GAL.Model';
declare namespace loc='http://www.telefonica.com/wsdl/UNICA/SOAP/OProv_Management/v6/local';
//gvp1:UniqueId

con:mockOperation.con:dispatchPath:
// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )

// get arguments and sum
def argUniqueId = holder.getNodeValue( "//gvp:uniqueId" )

if(argUniqueId == "UserDoesNotExist"){	
	return "UserDoesNotExist"	
}
else if(argUniqueId == "UserIsAlreadyEnabled"){
	return "UserIsAlreadyEnabled"	
}
else if(argUniqueId == "userX"){
	return "UserIsAlreadyEnabled"	
}
else if(argUniqueId == "UserIsAlreadyDisabled"){
	return "UserIsAlreadyDisabled"
}
else if(argUniqueId == "CannotDeleteEnabledUser"){
	return "CannotDeleteEnabledUser"
}
else if(argUniqueId == "MultipleErrorsDetectedByUMG"){
	return "MultipleErrorsDetectedByUMG"
}
else if(argUniqueId == "AccountAlreadyExists"){
	return "AccountAlreadyExists"
}
else if(argUniqueId == "PrincipalIdDoesNotExist"){
	return "PrincipalIdDoesNotExist"
}
else if(argUniqueId == "UnknownError"){
	return "UnknownError"
}
else if(argUniqueId == "InvalidPassword"){
	return "InvalidPassword"

}else{
	requestContext.UniqueId = argUniqueId
	return "OK"
}

