// get arguments
def argUniqueId = holder.getNodeValue( "//gvp:uniqueId" )

if(argUniqueId == "already_disabled_user" | argUniqueId == "UserIsAlreadyDisabled")
	return "UserIsAlreadyDisabled"
else if(argUniqueId == "UserAccountDoesNotExist")
	return "UserAccountDoesNotExist"
else if(argUniqueId == "user1")
	return "OK"
else if(argUniqueId == "userX")
	return "OK"
else{
	return "UserDoesNotExist"
}

